package Assignment1;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i,j,m;
		
		System.out.println("Multiplication table 1-10\n");
		
		for(i=1;i<=10;i++) {
			
			for(j=1;j<=10;j++) {
				
				System.out.println(i+" * "+j+" = "+ i*j);
				
			}System.out.println("\n");
		}

	}

}
